svg.script:

var svg_ns = "http://www.w3.org/2000/svg";
var xlink_ns = "http://www.w3.org/1999/xlink";

var movement_id;
var movement_done = 0;

var top_card;
hand = new Array;
var players = new Object;

var current_player;

set_up();

function set_up() {
    add_start_button();
    add_bot_button();
}

function add_start_button() {
  button = document.createElementNS(svg_ns, "use");
  button.setAttributeNS(xlink_ns, "xlink:href", "#start-game");
  button.setAttribute("x", 75);
  button.setAttribute("y", 200);
  button.setAttribute("id", "button-start-game");
  document.firstChild.appendChild(button);
}

function add_bot_button() {
  button = document.createElementNS(svg_ns, "use");
  button.setAttributeNS(xlink_ns, "xlink:href", "#add-bot");
  button.setAttribute("x", 250);
  button.setAttribute("y", 200);
  button.setAttribute("id", "button-add-bot");
  document.firstChild.appendChild(button);
}

function clicked_bot_button() {
  add_bot();
  remove("button-add-bot");
}

function clicked_start_button() {
  start_game();
  remove_buttons();
  display_loading_message();
}

game.start_game = function() {
  rpc("get_full_state");
}

game.set_current_player = function(player) {
  current_player = player;
}

game.start_turn = function(player) {
  current_player = player;
  display_players();
}

game.receive_hand = function(cards) {
  hand = cards;
  display_hand();
}

// Todo: sort hand into a local array, and display _that_.
function display_hand() {
  remove_loading_message();
  for (card_index in hand) {
      card_id = hand[card_index];
      var card = put_card(card_id, 0 + 20 * card_index, 0, "hand-" + card_id);
      card.setAttribute("id", "hand-" + card_id);
      card.setAttribute("onclick", "play_card(evt)");
      card.setAttribute("onmouseover", "highlight_card(evt)");
      card.setAttribute("onmouseout", "unhighlight_card(evt)");
  }
}

// This should be animated.
function display_loading_message() {
  message = document.createElementNS(svg_ns, "use");
  message.setAttributeNS(xlink_ns, "xlink:href", "#LOADING-RED");
  message.setAttribute("y", 200);
  message.setAttribute("x", 150);
  message.setAttribute("id", "loading-message");
  document.firstChild.appendChild(message);
}

function remove_loading_message() {
  remove("loading-message");
}

function display_players() {
  var y = 165;
  remove("player-list");
  var group = document.createElementNS(svg_ns, "g");
  group.setAttribute("y", 100);
  group.setAttribute("x", 100);
  group.setAttribute("id", "player-list");
  for (player_name in players) {
        hand_size = players[player_name].card_count;
	last_card_id = players[player_name].last_card;
        var player_text = document.createElementNS(svg_ns, "text");
        var text_color = "black";
        if (player_name == current_player) {
          text_color = "red";
        }
        player_text.setAttribute("style", "font-size:12; fill:" + text_color + ";");
	player_text.setAttribute("x",15);
	player_text.setAttribute("y", y);
        var text = document.createTextNode(player_name + ": " + players[player_name].card_count);
        player_text.appendChild(text);
        group.appendChild(player_text);
	y = y + 15;

  }
  document.firstChild.appendChild(group);
}

function put_card(symbol_id, x, y, use_id) {
      var card;
      if (!(use_id && (card = document.getElementById(use_id)))) {
  	   card = document.createElementNS(svg_ns, "use");
           card.setAttributeNS(xlink_ns, "xlink:href", "#" + symbol_id);
           document.firstChild.appendChild(card);
	   if (use_id) {
             card.setAttribute("id", use_id);
           }
      }
      card.setAttribute("x", x);
      card.setAttribute("y", y);
      return card;
}

game.starter_card = function(card_id) {
  card = put_card(card_id, 300, 300, "starter");
  top_card = card;
}

// The circumlocution below is to get around bizarre behavior when cards
// are drawn before the player makes his first play.
game.draw_card = function(card_id) {
   var new_hand = new Array;
   players[info.nickname].card_count = players[info.nickname].card_count + 1;
   display_players();
   for (index in hand) {
     new_hand[index] = hand[index];
   }
   new_hand[count_array(new_hand)] = card_id;
   hand = new_hand;
   animate_card_draw(card_id);
}

function animate_card_draw(card_id) {
    display_hand();
}

game.player_drew_card = function(player) {
  players[player].card_count = players[player].card_count + 1;
  display_players();
}

function remove_buttons() {
  remove_button("start-game");
  remove_button("add-bot");
}

function remove_button(type) {
  remove("button-" + type);
}

function play_card(evt) {
    clicked = evt.getTarget();
    card_symbol = clicked.parentNode;
    card_name = card_symbol.getAttribute("id");
    rpc("play_card", card_name);
    var rank = card_name.substr(0,1);
    if (rank - 0 == 8)
       show_suit_panel();
}

function highlight_card(evt) {
    clicked = evt.getTarget();
    card_symbol = clicked.parentNode;
    card_id = "hand-" + card_symbol.getAttribute("id");
    card = document.getElementById(card_id);
    card.setAttribute("y", 5);
}

function unhighlight_card(evt) {
    clicked = evt.getTarget();
    card_symbol = clicked.parentNode;
    card_id = "hand-" + card_symbol.getAttribute("id");
    card = document.getElementById(card_id);
    card.setAttribute("y", 0);
}


function show_suit_panel() {
   panel = document.createElementNS(svg_ns, "use");
   panel.setAttributeNS(xlink_ns, "xlink:href", "#suit-panel");
   panel.setAttribute("id", "suit-panel-use");
   panel.setAttribute("x", 100);
   panel.setAttribute("y", 100);
   document.firstChild.appendChild(panel);
}

function hide_suit_panel() {
    remove("suit-panel-use");
}

function choose_suit(evt) {
    clicked = evt.getTarget();
    var id = clicked.id;
    var chosen_suit = id.substr(7,1);
    rpc("choose_suit", chosen_suit);
    hide_suit_panel();
}

game.player_played_card = function(player, card_id) {
    var new_top_card;
    remove("suit-balloon");
    players[player].card_count = players[player].card_count - 1;
    if (player == info.nickname) {
       new_top_card = document.getElementById("hand-" + card_id);
       animate_card_play(new_top_card);
       // Eliminate the highlight animations.
       new_top_card.setAttribute("onmouseout", "");
       new_top_card.setAttribute("onmouseover", "");
       remove_card_from_hand(card_id);
       display_hand();
    } else {
       new_top_card = put_card(card_id, 300, 300);
       players[player].last_card = card_id;
       display_players();
    }
    replace_top_card(new_top_card);
}

game.player_chose_suit = function(player, suit) {
  var balloon_name = suit + "-BALLOON";
  var balloon = document.createElementNS(svg_ns, "use");
  balloon.setAttributeNS(xlink_ns, "xlink:href", "#"+balloon_name);
  balloon.setAttribute("id", "suit-balloon");
  balloon.setAttribute("x", 195);
  balloon.setAttribute("y", 195);
  document.firstChild.appendChild(balloon);
}

// This is a dumb function... how better to do this in JS?
function remove_card_from_hand(card_name) {
    var new_hand = new Array;
    var new_hand_index = 0;
    var success = 0;
    for (index in hand) {
       if (!(hand[index] == card_name)) {
           new_hand[new_hand_index++] = hand[index];             
       } else {
           success = 1;
       }
    }
    hand = new_hand;
    return success;
}

replace_top_card = function(card) {
    if (top_card) {
       old_id = top_card.getAttribute("id");
       remove(old_id);
    }
    top_card = card;
}

function remove(id) {
  element = document.getElementById(id);
  if (element != null) {
    document.firstChild.removeChild(element);
  }
}

game.set_player_hand_size = function(player, hand_size) {
    if (!(players[player])) {
	players[player] = {};
    }
    players[player].card_count = hand_size;
}

function animate_card_play(card) {
    frames = 5;
    dest_x = 300;
    dest_y = 300;
    teleport_card(card, dest_x, dest_y);
}

function teleport_card(card, x, y) {
    card.setAttribute("x", x);
    card.setAttribute("y", y);
}

function move_card(card, x_step, y_step) {
    if (!movement_done) {
      current_x = card.getAttribute("x");
      current_y = card.getAttribute("y");
      card.setAttribute("x", current_x - 0 + x_step);
      card.setAttribute("y", current_y - 0 + y_step);
      if (card.getAttribute("x") >= 300) {
          clearInterval(movement_id);
       	  movement_done = 1;
      }
    }
}

// I don't know how to do this for real!! :(
function count_array(array) {
    var count = 0;
    for (member in array) { count++ }
    return count;
}

game.scores = function(scores) {
    for (player in scores) {
	window.alert(player + " got " + scores[player] + " points.");
    }
}


function end_game() {
    window.alert("Game over!");
    match_card = null;
    match_suit = null;
}


